import 'dart:ui';

import 'package:flutter/material.dart';
import 'package:my_portfolio/components/custom_colors.dart';
import 'package:my_portfolio/components/riveutils.dart';
import 'package:rive/rive.dart';

import 'components/animated_bar.dart';
import 'components/custom_navigationbar.dart';

class HomePage extends StatefulWidget {
  const HomePage({super.key});

  @override
  State<HomePage> createState() => _HomePageState();
}

class _HomePageState extends State<HomePage> {
  RiveAsset selectedBottomNavigation = bottomNavigation.first;
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      resizeToAvoidBottomInset: false,
      extendBody: true,
      bottomNavigationBar: SafeArea(
        child: Container(
          padding: const EdgeInsets.all(12),
          margin: const EdgeInsets.fromLTRB(300, 0, 300, 15),
          height: 64,
          decoration: BoxDecoration(
            color: CustomColors().customBlue.withOpacity(0.97),
            borderRadius: const BorderRadius.all(
              Radius.circular(24),
            ),
          ),
          child: Row(
            mainAxisAlignment: MainAxisAlignment.spaceBetween,
            children: [
              ...List.generate(
                bottomNavigation.length,
                (index) => GestureDetector(
                  onTap: () {
                    bottomNavigation[index].input!.change(true);
                    if (bottomNavigation[index] != selectedBottomNavigation) {
                      setState(() {
                        selectedBottomNavigation = bottomNavigation[index];
                      });
                    }
                    Future.delayed(
                      const Duration(seconds: 1),
                      () {
                        bottomNavigation[index].input!.change(false);
                      },
                    );
                  },
                  child: Column(
                    mainAxisSize: MainAxisSize.min,
                    children: [
                      AnimatedBar(
                        isActive:
                            bottomNavigation[index] == selectedBottomNavigation,
                      ),
                      SizedBox(
                        height: 36,
                        width: 36,
                        child: Opacity(
                          opacity: bottomNavigation[index] ==
                                  selectedBottomNavigation
                              ? 1
                              : 0.5,
                          child: RiveAnimation.asset(
                            bottomNavigation.first.src,
                            artboard: bottomNavigation[index].artboard,
                            onInit: (artboard) {
                              StateMachineController controller =
                                  RiveUtils.getRiveController(artboard,
                                      StateMachineName: bottomNavigation[index]
                                          .stateMachineName);
                              bottomNavigation[index].input =
                                  controller.findSMI("active") as SMIBool;
                            },
                          ),
                        ),
                      ),
                    ],
                  ),
                ),
              ),
            ],
          ),
        ),
      ),
